if(!(int)$vbulletin->userinfo['userid'])
{
	$reg_ref = '';
	switch(THIS_SCRIPT)
	{
		case 'newthread':
			echo 'This is newthread page';
			$reg_ref = 'reg-new-thread';
			break;
		case 'newreply':
			//if url contains "noquote=1", that means its not quote, else its quote
			var_dump($vbulletin->GPC);
			if($vbulletin->GPC['noquote'] == 1)
			{
				echo 'This is newreply page with noquote';
				$reg_ref = 'reg-post-reply';
			}
			else if($vbulletin->GPC['quoteall'] == 1)
			{
				echo 'This is newreply page with quote all';
				$reg_ref = 'reg-reply-quote-all';
			}
			else if(isset($vbulletin->GPC['vbulletin_multiquote'])&& !empty($vbulletin->GPC['vbulletin_multiquote']))
			{
				echo 'This is newreply page with multi quote';
				$reg_ref = 'reg-reply-multi-quote';
			}
			else
			{
				// if quote/:
				echo 'This is newreply page with quote';
				$reg_ref = 'reg-reply-quote';
			}
			break;
		case 'memberlist':
			echo 'This is memberlist page';
			$reg_ref = 'reg-member-list';
			break;
		case 'garage':
			echo 'This is garage page';
			$reg_ref = 'reg-garage';
			break;
		case 'album':
			echo 'This is album page';
			$reg_ref = 'reg-add-album';
			break;
		case 'picturecomment':
			echo 'This is picturecomment page';
			$reg_ref = 'reg-picture-comment';
			break;
	}
die;
